import React, { useEffect, useState } from "react";
import '../components/styles/Container.css';
import { useAuth } from "../Account/AuthUser";
import api from '../Account/api';
import '../components/styles/Settings.css';

export default function Settings() {
    const { user, setUser } = useAuth();

    // 1) Основные поля профиля
    const [formData, setFormData] = useState({
        id:        '',
        username:  '',
        age:       '',
        gender:    '',
        password:  ''
    });

    // 2) Отдельное состояние для notification preferences
    const [notifications, setNotifications] = useState({
        profileIncomplete: true,
        systemAlerts:      true,
        marketingEmails:   false
    });

    const [avatarFile,    setAvatarFile]    = useState(null);
    const [avatarPreview, setAvatarPreview] = useState('');
    const [message,       setMessage]       = useState('');
    const [error,         setError]         = useState('');

    // Заполняем форму и notification prefs из user при загрузке
    useEffect(() => {
        if (!user) return;

        setFormData({
            id:       user.id,
            username: user.username || '',
            age:      user.age || '',
            gender:   user.gender || '',
            password: ''
        });

        setAvatarPreview(user.avatarUrl || '');

        // Если сервер возвращает user.notifications
        if (user.notifications) {
            setNotifications(user.notifications);
        }
    }, [user]);

    const handleChange = (e) => {
        setFormData(prev => ({
            ...prev,
            [e.target.name]: e.target.value
        }));
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setAvatarFile(file);
            setAvatarPreview(URL.createObjectURL(file));
        }
    };

    // Переключить одну из prefs
    const toggleNotification = (key) => {
        setNotifications(prev => ({
            ...prev,
            [key]: !prev[key]
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        try {
            // 1) обновляем профиль + уведомления
            const res = await api.put(`/api/users/update/${formData.id}`, {
                username:      formData.username,
                age:           formData.age,
                gender:        formData.gender,
                password:      formData.password,
                notifications: notifications
            });
            console.log('Ответ сервера (обновление):', res.data);
            setUser({ ...user, ...res.data });

            // 2) если аватар выбран — загружаем
            if (avatarFile) {
                const avatarData = new FormData();
                avatarData.append('avatar', avatarFile);

                const avatarRes = await api.post(
                    `/api/users/uploadAvatar/${formData.id}`,
                    avatarData,
                    {
                        headers: {
                            'Content-Type': 'multipart/form-data',
                            Authorization: `Bearer ${user.token}`,
                        },
                    }
                );
                console.log('Ответ сервера (аватар):', avatarRes.data);
                setUser(prev => ({ ...prev, avatarUrl: avatarRes.data.avatarUrl }));
                setAvatarPreview(avatarRes.data.avatarUrl);
            }

            setMessage('Данные успешно сохранены');
        } catch (err) {
            console.error('Ошибка при обновлении:', err.response || err);
            setError(err.response?.data?.error || 'Ошибка на сервере');
        }
    };

    return (
        <div className="containerforall">
            <h2>Настройки</h2>
            <form onSubmit={handleSubmit}>
                {/* Аватарка */}
                <div>
                    <label>Аватар:</label><br/>
                    {avatarPreview && (
                        <img
                            src={avatarPreview}
                            alt="Avatar preview"
                            style={{
                                width: 100,
                                height: 100,
                                objectFit: 'cover',
                                borderRadius: '50%'
                            }}
                        />
                    )}
                    <input type="file" accept="image/*" onChange={handleAvatarChange}/>
                </div>

                {/* Поля профиля */}
                <div>
                    <label>Имя:</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Возраст:</label>
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Пол:</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Выбери пол</option>
                        <option value="male">Мужской</option>
                        <option value="female">Женский</option>
                        <option value="other">Другое</option>
                    </select>
                </div>
                <div>
                    <label>Новый пароль:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                </div>

                {/* Настройки уведомлений */}
                <fieldset style={{ marginTop: '1em' }}>
                    <legend>Настройки уведомлений</legend>
                    <label>
                        <input
                            type="checkbox"
                            checked={notifications.profileIncomplete}
                            onChange={() => toggleNotification('profileIncomplete')}
                        />
                        Напоминать о неполном профиле
                    </label><br/>
                    <label>
                        <input
                            type="checkbox"
                            checked={notifications.systemAlerts}
                            onChange={() => toggleNotification('systemAlerts')}
                        />
                        Системные уведомления
                    </label><br/>
                    <label>
                        <input
                            type="checkbox"
                            checked={notifications.marketingEmails}
                            onChange={() => toggleNotification('marketingEmails')}
                        />
                        Маркетинговые письма
                    </label>
                </fieldset>

                <button type="submit" style={{ marginTop: '1em' }}>
                    Сохранить изменения
                </button>
                {message && <p style={{ color: 'green' }}>{message}</p>}
                {error   && <p style={{ color: 'red' }}>{error}</p>}
            </form>
        </div>
    );
}
