import React, { useEffect } from 'react';
import { useAuth } from '../Account/AuthUser';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user } = useAuth();

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user?.avatar ? SERVER_URL + user.avatar : '/default.png';

    useEffect(() => {
        // Запрашиваем разрешение на показ пуш уведомлений
        if (user && (!user.age || !user.gender) && "Notification" in window) {
            if (Notification.permission === "default") {
                Notification.requestPermission().then(permission => {
                    if (permission === "granted") {
                        showNotification();
                    }
                });
            } else if (Notification.permission === "granted") {
                showNotification();
            }
        }

        function showNotification() {
            new Notification("Profile incomplete", {
                body: "Please add your age and/or gender to complete your profile.",
                icon: "/default.png"  // Можно заменить на иконку
            });
        }
    }, [user]);

    if (!user) {
        return <div className="profile-container">Not logged in</div>;
    }

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img
                    src={avatarSrc}
                    alt="Avatar"
                    className="profile-avatar"
                    style={{ width: 150, height: 150, borderRadius: '50%', objectFit: 'cover' }}
                />
                <h2>Hi, {user.username}!</h2>
            </div>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role || 'User'}</p>
            {user.age !== undefined && <p><strong>Age:</strong> {user.age}</p>}
            {user.gender !== undefined && <p><strong>Gender:</strong> {user.gender}</p>}
        </div>
    );
}
