import React, { useEffect, useState } from 'react';
import { useAuth } from '../Account/AuthUser';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user } = useAuth();
    const [missingFields, setMissingFields] = useState([]);
    const [showAlert, setShowAlert] = useState(false);

    if (!user) {
        return <div className="profile-container">Not logged in</div>;
    }

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user.avatar ? SERVER_URL + user.avatar : '/default-avatar.png';

    const notification = user.notification || { push: false, inApp: false };

    // Строка включённых типов уведомлений
    const enabledChannels = [];
    if (notification.push) enabledChannels.push('Push');
    if (notification.inApp) enabledChannels.push('In-App');
    const notificationText = enabledChannels.length
        ? enabledChannels.join(', ')
        : 'Нет включённых уведомлений';

    useEffect(() => {
        const missing = [];
        if (!user.age) missing.push('возраст');
        if (!user.gender) missing.push('пол');
        setMissingFields(missing);
        setShowAlert(missing.length > 0);
    }, [user]);

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img
                    src={avatarSrc}
                    alt="Avatar"
                    className="profile-avatar"
                    style={{
                        width: 150,
                        height: 150,
                        borderRadius: '50%',
                        objectFit: 'cover'
                    }}
                />
                <h2>Hi, {user.username}!</h2>
            </div>

            {showAlert && (
                <div className="notification-alert" style={{
                    backgroundColor: '#ffe5e5',
                    padding: '10px',
                    borderRadius: '8px',
                    marginBottom: '15px',
                    color: '#990000',
                    fontWeight: 'bold'
                }}>
                    Пожалуйста, укажите {missingFields.join(' и ')} в настройках профиля!
                </div>
            )}

            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role || 'User'}</p>
            {user.age && <p><strong>Age:</strong> {user.age}</p>}
            {user.gender && <p><strong>Gender:</strong> {user.gender}</p>}
            <p><strong>Уведомления:</strong> {notificationText}</p>
        </div>
    );
}
