import React, { useEffect, useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import api from '../Account/api';
import '../components/styles/Container.css';

export default function Dashboard() {
    const navigate = useNavigate();
    const { t } = useTranslation();

    const [users, setUsers] = useState([]);
    const [courses, setCourses] = useState([]);
    const [editingUser, setEditingUser] = useState(null);
    const [editingCourse, setEditingCourse] = useState(null);
    const [creatingCourse, setCreatingCourse] = useState(false);
    const [userForm, setUserForm] = useState({ username: '', age: '', gender: '', role: 'user', password: '' });
    const [avatarPreview, setAvatarPreview] = useState('');
    const [avatarFile, setAvatarFile] = useState(null);
    const [courseForm, setCourseForm] = useState({ title: '', description: '', image: '', category: 'other', price: '' });
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUsers();
        fetchCourses();
    }, []);

    const fetchUsers = async () => {
        try {
            const res = await api.get('/api/users');
            setUsers(res.data);
        } catch (err) {
            console.error('Error fetching users:', err);
        }
    };

    const fetchCourses = async () => {
        try {
            const res = await api.get('/api/courses?page=1&limit=100');
            setCourses(res.data.courses);
        } catch (err) {
            console.error('Error fetching courses:', err);
        }
    };

    const handleUserDelete = async (id) => {
        try {
            await api.delete(`/api/users/${id}`);
            setUsers(users.filter((u) => u.id !== id));
        } catch (err) {
            console.error('Error deleting user:', err);
        }
    };

    const handleCourseDelete = async (id) => {
        try {
            await api.delete(`/api/courses/${id}`);
            setCourses(courses.filter((c) => c.id !== id));
        } catch (err) {
            console.error('Error deleting course:', err);
        }
    };

    const handleEditUserClick = (user) => {
        setEditingUser(user);
        setEditingCourse(null);
        setCreatingCourse(false);
        setMessage('');
        setError('');
        setUserForm({ username: user.username || '', age: user.age || '', gender: user.gender || '', role: user.role || 'user', password: '' });
        setAvatarPreview(user.avatar || '');
        setAvatarFile(null);
    };

    const handleUserFormChange = (e) => setUserForm({ ...userForm, [e.target.name]: e.target.value });

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setAvatarFile(file);
            setAvatarPreview(URL.createObjectURL(file));
        }
    };

    const handleUpdateUser = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            const { username, age, gender, password, role } = userForm;
            await api.put(`/api/users/admin/update/${editingUser.id}`, { username, age, gender, password, role });
            if (avatarFile) {
                const formData = new FormData();
                formData.append('avatar', avatarFile);
                await api.post(`/api/users/uploadAvatar/${editingUser.id}`, formData, { headers: { 'Content-Type': 'multipart/form-data' } });
            }
            await fetchUsers();
            setEditingUser(null);
            setMessage(t('dashboard.userUpdated'));
        } catch (err) {
            console.error(err);
            setError(t('dashboard.updateError'));
        }
    };

    const handleCreateCourseClick = () => {
        setCreatingCourse(true);
        setEditingUser(null);
        setEditingCourse(null);
        setMessage('');
        setError('');
        setCourseForm({ title: '', description: '', image: '', category: 'other', price: '' });
    };

    const handleCourseFormChange = (e) => setCourseForm({ ...courseForm, [e.target.name]: e.target.value });

    const handleCreateCourseSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            await api.post('/api/courses', courseForm);
            await fetchCourses();
            setCreatingCourse(false);
            setMessage(t('dashboard.courseCreated'));
        } catch (err) {
            console.error(err);
            setError(t('dashboard.createError'));
        }
    };

    const handleEditCourseClick = (course) => {
        setEditingCourse(course);
        setEditingUser(null);
        setCreatingCourse(false);
        setMessage('');
        setError('');
        setCourseForm({ title: course.title || '', description: course.description || '', image: course.image || '', category: course.category || 'other', price: course.price || '' });
    };

    const handleUpdateCourse = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            await api.put(`/api/courses/${editingCourse.id}`, courseForm);
            await fetchCourses();
            setEditingCourse(null);
            setMessage(t('dashboard.courseUpdated'));
        } catch (err) {
            console.error(err);
            setError(t('dashboard.updateError'));
        }
    };

    const handleCancel = () => {
        setEditingUser(null);
        setEditingCourse(null);
        setCreatingCourse(false);
        setMessage('');
        setError('');
    };

    return (
        <div className="containerforall">
            <h1>{t('dashboard.title')}</h1>

            <section>
                <h2>{t('dashboard.users')}</h2>
                <table>
                    <thead>
                    <tr>
                        <th>{t('dashboard.id')}</th>
                        <th>{t('dashboard.username')}</th>
                        <th>{t('dashboard.email')}</th>
                        <th>{t('dashboard.role')}</th>
                        <th>{t('dashboard.actions')}</th>
                    </tr>
                    </thead>
                    <tbody>
                    {users.map((u) => (
                        <tr key={u.id}>
                            <td>{u.id}</td>
                            <td>{u.username}</td>
                            <td>{u.email}</td>
                            <td>{u.role}</td>
                            <td>
                                <button onClick={() => handleEditUserClick(u)}>{t('dashboard.edit')}</button>
                                <button onClick={() => handleUserDelete(u.id)}>{t('dashboard.delete')}</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </section>

            <section>
                <h2>{t('dashboard.courses')}</h2>
                <button onClick={handleCreateCourseClick}>{t('dashboard.createCourse')}</button>
                <table>
                    <thead>
                    <tr>
                        <th>{t('dashboard.id')}</th>
                        <th>{t('dashboard.courseTitle')}</th>
                        <th>{t('dashboard.courseCategory')}</th>
                        <th>{t('dashboard.coursePrice')}</th>
                        <th>{t('dashboard.actions')}</th>
                    </tr>
                    </thead>
                    <tbody>
                    {courses.map((c) => (
                        <tr key={c.id}>
                            <td>{c.id}</td>
                            <td>{c.title}</td>
                            <td>{c.category}</td>
                            <td>{c.price}</td>
                            <td>
                                <button onClick={() => handleEditCourseClick(c)}>{t('dashboard.edit')}</button>
                                <button onClick={() => handleCourseDelete(c.id)}>{t('dashboard.delete')}</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </section>

            {/* Редактирование/создание */}
            {(editingUser || creatingCourse || editingCourse) && (
                <section style={{ marginTop: 20, padding: 10, border: '1px solid #ccc' }}>
                    {/* Можно аналогично применить t() к остальным формам */}
                    {message && <p style={{ color: 'green' }}>{message}</p>}
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                </section>
            )}

            <Outlet />
        </div>
    );
}
