import React, { useEffect, useState } from "react";
import '../components/styles/Settings.css'; // обновим путь
import { useAuth } from "../Account/AuthUser";
import api from '../Account/api';

export default function Settings() {
    const { user, setUser } = useAuth();
    const [formData, setFormData] = useState({
        id: '',
        username: '',
        age: '',
        gender: '',
        password: ''
    });
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        if (user) {
            setFormData({
                id: user.id,
                username: user.username || '',
                age: user.age || '',
                gender: user.gender || '',
                password: ''
            });
        }
    }, [user]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            const res = await api.put('/api/auth/updateUser', formData);
            setMessage('✅ Данные успешно сохранены');
        } catch (err) {
            setError(err.response?.data?.error || '❌ Ошибка на сервере');
        }
    };

    return (
        <div className="settings-container">
            <h2 className="settings-title">Настройки профиля</h2>
            <form className="settings-form" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Имя</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Возраст</label>
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Пол</label>
                    <select name="gender" value={formData.gender} onChange={handleChange} required>
                        <option value="">Выберите</option>
                        <option value="male">Мужской</option>
                        <option value="female">Женский</option>
                        <option value="other">Другое</option>
                    </select>
                </div>
                <div className="form-group">
                    <label>Новый пароль</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        placeholder="Оставьте пустым, если не хотите менять"
                    />
                </div>
                <button type="submit" className="save-button">💾 Сохранить</button>
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
}
