import React, { useState, useEffect } from 'react';
import { useAuth } from '../Account/AuthUser';
import '../components/styles/Profile.css';

// Notification component for in-app toasts
const Notification = ({ message, type, onClose }) => {
    useEffect(() => {
        const timer = setTimeout(() => {
            console.log('Auto-closing notification:', message);
            onClose();
        }, 5000); // Auto-dismiss after 5 seconds
        return () => clearTimeout(timer);
    }, [onClose, message]);

    const handleClose = () => {
        console.log('Close button clicked for notification:', message);
        onClose();
    };

    return (
        <div
            className={`notification fixed top-4 right-4 p-4 rounded-lg shadow-lg text-white z-50 ${
                type === 'error' ? 'bg-red-500' : 'bg-green-500'
            }`}
        >
            {message}
            <button
                className="ml-4 text-white font-bold focus:outline-none hover:bg-opacity-80"
                onClick={handleClose}
                aria-label="Закрыть уведомление"
            >
                ×
            </button>
        </div>
    );
};

export default function Profile() {
    const { user } = useAuth();
    const [notifications, setNotifications] = useState([]);

    // Function to add a notification
    const addNotification = (message, type = 'info') => {
        const id = Math.random().toString(36).substr(2, 9);
        console.log('Adding notification:', message, type, id);
        setNotifications((prev) => [...prev, { id, message, type }]);

        // Безопасная проверка для push-уведомлений
        if (typeof window !== 'undefined' && window.Notification && Notification.permission) {
            if (Notification.permission === 'granted') {
                try {
                    new Notification(message);
                } catch (err) {
                    console.error('Ошибка при создании push-уведомления:', err);
                }
            } else if (Notification.permission !== 'denied') {
                try {
                    if (typeof Notification.requestPermission === 'function') {
                        Notification.requestPermission().then((permission) => {
                            if (permission === 'granted') {
                                new Notification(message);
                            }
                        }).catch((err) => {
                            console.error('Ошибка при запросе разрешения на уведомления:', err);
                        });
                    } else {
                        console.warn('Notification.requestPermission не поддерживается');
                    }
                } catch (err) {
                    console.error('Ошибка при обработке push-уведомлений:', err);
                }
            }
        } else {
            console.warn('Push-уведомления не поддерживаются в этом окружении');
        }
    };

    // Remove notification by ID
    const removeNotification = (id) => {
        console.log('Removing notification with ID:', id);
        setNotifications((prev) => prev.filter((notif) => notif.id !== id));
    };

    // Check for missing age or gender on mount
    useEffect(() => {
        if (!user) return;
        if (user.age === undefined || user.age === null || user.age === '') {
            addNotification('Пожалуйста, укажите ваш возраст, чтобы завершить профиль.', 'error');
        }
        if (!user.gender) {
            addNotification('Пожалуйста, укажите ваш пол, чтобы завершить профиль.', 'error');
        }
    }, [user]);

    if (!user) {
        return (
            <div className="profile-container">
                Не авторизован
                <div className="notification-container fixed top-0 right-0 z-50">
                    {notifications.map((notif) => (
                        <Notification
                            key={notif.id}
                            message={notif.message}
                            type={notif.type}
                            onClose={() => removeNotification(notif.id)}
                        />
                    ))}
                </div>
            </div>
        );
    }

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user.avatar ? SERVER_URL + user.avatar : '/default-avatar.png';
    console.log('User:', user);
    console.log('Avatar URL:', avatarSrc);

    return (
        <div className="profile-container max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md relative z-10">
            <div className="profile-header flex flex-col items-center">
                <img
                    src={avatarSrc}
                    alt="Avatar"
                    className="profile-avatar w-32 h-32 rounded-full object-cover mb-4"
                />
                <h2 className="text-2xl font-bold text-gray-800">Привет, {user.username}!</h2>
            </div>
            <div className="profile-details mt-4">
                <p className="text-gray-600"><strong>Email:</strong> {user.email}</p>
                <p className="text-gray-600"><strong>Роль:</strong> {user.role || 'Пользователь'}</p>
                {user.age !== undefined && user.age !== '' && (
                    <p className="text-gray-600"><strong>Возраст:</strong> {user.age}</p>
                )}
                {user.gender && (
                    <p className="text-gray-600"><strong>Пол:</strong> {user.gender}</p>
                )}
            </div>
            <div className="notification-container fixed top-0 right-0 z-50">
                {notifications.map((notif) => (
                    <Notification
                        key={notif.id}
                        message={notif.message}
                        type={notif.type}
                        onClose={() => removeNotification(notif.id)}
                    />
                ))}
            </div>
        </div>
    );
}
