import React, { useEffect, useState } from 'react';
import { useAuth } from '../Account/AuthUser';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user } = useAuth();
    const [showToast, setShowToast] = useState(false);

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user?.avatar ? SERVER_URL + user.avatar : '/default.png';

    useEffect(() => {
        if (user && (!user.age || !user.gender)) {
            setShowToast(true);

            // Отправка уведомления в БД
            fetch(`${SERVER_URL}/notifications`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    userId: user.id,
                    type: { push: false, inApp: true },
                    message: 'Please complete your profile: add age and/or gender.'
                })
            }).catch(err => console.error('Notification error:', err));

            // Скрытие тоста через 5 секунд
            const timer = setTimeout(() => {
                setShowToast(false);
            }, 5000);

            return () => clearTimeout(timer);
        }
    }, [user]);

    if (!user) {
        return <div className="profile-container">Not logged in</div>;
    }

    return (
        <div className="profile-container">
            {showToast && (
                <div className="toast">
                    ⚠️ Please complete your profile: age or gender is missing.
                </div>
            )}

            <div className="profile-header">
                <img
                    src={avatarSrc}
                    alt="Avatar"
                    className="profile-avatar"
                    style={{ width: 150, height: 150, borderRadius: '50%', objectFit: 'cover' }}
                />
                <h2>Hi, {user.username}!</h2>
            </div>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role || 'User'}</p>
            {user.age !== undefined && <p><strong>Age:</strong> {user.age}</p>}
            {user.gender !== undefined && <p><strong>Gender:</strong> {user.gender}</p>}
        </div>
    );
}
