import React, {useEffect, useState} from 'react';
import {useAuth} from '../Account/AuthUser';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user } = useAuth();
    const [avatarUrl, setAvatarUrl] = useState('/default-avatar.png');

    useEffect(() => {
        if (user?.avatar) {
            // Формируем полный URL к аватару из папки uploads
            const avatarPath = user.avatar.startsWith('/uploads/')
                ? user.avatar
                : `/uploads/${user.avatar}`;

            fetch(`http://localhost:5000${avatarPath}`)
                .then(res => {
                    if (!res.ok) throw new Error('Failed to load avatar');
                    return res.blob();
                })
                .then(blob => {
                    const url = URL.createObjectURL(blob);
                    setAvatarUrl(url);
                })
                .catch(() => setAvatarUrl('/default-avatar.png'));
        } else {
            setAvatarUrl('/default-avatar.png');
        }
    }, [user]);

    if (!user) return <div className="profile-container">Not logged in</div>;

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img
                    src={avatarUrl}
                    alt="Avatar"
                    className="profile-avatar"
                    style={{ width: 150, height: 150, borderRadius: '50%', objectFit: 'cover' }}
                />
                <h2>Hi, {user.username}!</h2>
            </div>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role || 'User'}</p>
            {user.age !== undefined && <p><strong>Age:</strong> {user.age}</p>}
            {user.gender && <p><strong>Gender:</strong> {user.gender}</p>}
        </div>
    );
}
