import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../Account/api';

const CourseComments = () => {
    const { id } = useParams(); // id курса из URL
    const [comments, setComments] = useState([]);
    const [courseTitle, setCourseTitle] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchComments = async () => {
            try {
                setLoading(true);
                setError(null);

                // Получаем курс (чтобы вывести название)
                const courseRes = await api.get(`/api/courses/${id}`);
                setCourseTitle(courseRes.data.title);

                // Получаем комментарии по курсу
                const commentsRes = await api.get(`/api/courses/${id}/comments`);
                setComments(commentsRes.data.comments);
            } catch (e) {
                setError('Failed to load comments');
            } finally {
                setLoading(false);
            }
        };

        fetchComments();
    }, [id]);

    if (loading) return <div>Loading comments...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="containerforall">
            <h2>Comments for: {courseTitle}</h2>
            <Link to="/courses" className="btn">Back to Catalog</Link>

            {comments.length === 0 ? (
                <p>No comments yet.</p>
            ) : (
                <ul className="comments-list">
                    {comments.map(c => (
                        <li key={c.id}>
                            <strong>{c.username || 'User'}:</strong> {c.comment} <br />
                            Rating: {c.rating}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default CourseComments;
