import React, { useState } from 'react';
import './Profile.css';

export default function Profile() {
    // Мок-данные пользователя (обычно берутся из useAuth)
    const user = {
        username: 'JohnDoe',
        email: 'john@example.com',
        role: 'User',
        avatar: '/avatars/john.png',
        age: null,        // Возраст отсутствует
        gender: '',       // Пол отсутствует
        notification: {
            inApp: true,
            push: true
        }
    };

    const [notificationPermission, setNotificationPermission] = useState(Notification.permission);

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user.avatar ? SERVER_URL + user.avatar : '/default-avatar.png';

    const canPushNotify = user.notification?.push === true;
    const canInAppNotify = user.notification?.inApp === true;

    // Показываем уведомление, если отсутствует хотя бы возраст или пол
    const missingProfileInfo = !user.age || !user.gender;

    const enableNotifications = () => {
        if (!canPushNotify) {
            alert("Push уведомления отключены в настройках пользователя.");
            return;
        }

        if (!("Notification" in window)) {
            alert("Ваш браузер не поддерживает уведомления.");
            return;
        }

        if (Notification.permission === "granted") {
            showNotification();
        } else if (Notification.permission !== "denied") {
            Notification.requestPermission().then(permission => {
                setNotificationPermission(permission);
                if (permission === "granted") {
                    showNotification();
                }
            });
        }
    };

    const showNotification = () => {
        new Notification("Привет!", {
            body: `Добро пожаловать, ${user.username}!`,
            icon: avatarSrc,
        });
    };

    return (
        <div className="profile-container" style={{ position: 'relative' }}>
            {canInAppNotify && missingProfileInfo && (
                <div className="inapp-notification">
                    Пожалуйста, заполните свой возраст и пол в настройках профиля для лучшего опыта.
                </div>
            )}

            <div>
                <div className="profile-header">
                    <img
                        src={avatarSrc}
                        alt="Avatar"
                        className="profile-avatar"
                        style={{ width: 150, height: 150 }}
                    />
                    <h2>Hi, {user.username}!</h2>
                </div>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Role:</strong> {user.role || 'User'}</p>
                {user.age !== undefined && user.age !== null && user.age !== '' && (
                    <p><strong>Age:</strong> {user.age}</p>
                )}
                {user.gender !== undefined && user.gender !== null && user.gender !== '' && (
                    <p><strong>Gender:</strong> {user.gender}</p>
                )}

                {canPushNotify ? (
                    <button
                        onClick={enableNotifications}
                        disabled={notificationPermission === 'granted'}
                        style={{ marginTop: 20 }}
                    >
                        {notificationPermission === 'granted' ? 'Уведомления включены' : 'Включить уведомления'}
                    </button>
                ) : (
                    <p style={{ marginTop: 20 }}>Push-уведомления отключены в настройках.</p>
                )}
            </div>
        </div>
    );
}
