import React, { useEffect, useState } from 'react';
import '../components/styles/Settings.css';
import { useAuth } from '../Account/AuthUser';
import api from '../Account/api';

// Notification component for in-app toasts
const Notification = ({ message, type, onClose }) => {
    useEffect(() => {
        const timer = setTimeout(() => {
            console.log('Auto-closing notification:', message);
            onClose();
        }, 5000); // Auto-dismiss after 5 seconds
        return () => clearTimeout(timer);
    }, [onClose, message]);

    const handleClose = () => {
        console.log('Close button clicked for notification:', message);
        onClose();
    };

    return (
        <div
            className={`notification fixed top-4 right-4 p-4 rounded-lg shadow-lg text-white z-50 ${
                type === 'error' ? 'bg-red-500' : 'bg-green-500'
            }`}
        >
            {message}
            <button
                className="ml-4 text-white font-bold focus:outline-none hover:bg-opacity-80"
                onClick={handleClose}
                aria-label="Закрыть уведомление"
            >
                ×
            </button>
        </div>
    );
};

export default function Settings() {
    const { user, setUser } = useAuth();
    const [formData, setFormData] = useState({
        id: '',
        username: '',
        age: '',
        gender: '',
        password: ''
    });
    const [avatarFile, setAvatarFile] = useState(null);
    const [avatarPreview, setAvatarPreview] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const [notifications, setNotifications] = useState([]);
    const [notificationPreferences, setNotificationPreferences] = useState({
        inApp: true,
        push: false, // Push notifications off by default
    });

    // Function to add a notification
    const addNotification = (message, type = 'info') => {
        if (notificationPreferences.inApp) {
            const id = Math.random().toString(36).substr(2, 9);
            console.log('Adding notification:', message, type, id);
            setNotifications((prev) => [...prev, { id, message, type }]);
        }
        if (notificationPreferences.push && typeof window !== 'undefined' && window.Notification && Notification.permission) {
            if (Notification.permission === 'granted') {
                try {
                    new Notification(message);
                } catch (err) {
                    console.error('Ошибка при создании push-уведомления:', err);
                }
            } else if (Notification.permission !== 'denied') {
                try {
                    if (typeof Notification.requestPermission === 'function') {
                        Notification.requestPermission().then((permission) => {
                            if (permission === 'granted') {
                                new Notification(message);
                            }
                        }).catch((err) => {
                            console.error('Ошибка при запросе разрешения на уведомления:', err);
                        });
                    } else {
                        console.warn('Notification.requestPermission не поддерживается');
                    }
                } catch (err) {
                    console.error('Ошибка при обработке push-уведомлений:', err);
                }
            }
        } else {
            console.warn('Push-уведомления не поддерживаются в этом окружении');
        }
    };

    // Remove notification by ID
    const removeNotification = (id) => {
        console.log('Removing notification with ID:', id);
        setNotifications((prev) => prev.filter((notif) => notif.id !== id));
    };

    // Check for missing age or gender on mount
    useEffect(() => {
        if (!user) return;
        if (user.age === undefined || user.age === null || user.age === '') {
            addNotification('Пожалуйста, укажите ваш возраст, чтобы завершить профиль.', 'error');
        }
        if (!user.gender) {
            addNotification('Пожалуйста, укажите ваш пол, чтобы завершить профиль.', 'error');
        }
    }, [user]);

    // Toggle notification preferences
    const togglePreference = (type) => {
        console.log('Toggling preference:', type);
        setNotificationPreferences((prev) => ({
            ...prev,
            [type]: !prev[type],
        }));
    };

    useEffect(() => {
        if (user) {
            setFormData({
                id: user.id || '',
                username: user.username || '',
                age: user.age || '',
                gender: user.gender || '',
                password: ''
            });
            setAvatarPreview(user.avatarUrl || '');
        }
    }, [user]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Обработчик выбора файла аватара
    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setAvatarFile(file);
            setAvatarPreview(URL.createObjectURL(file)); // показываем превью
        }
    };

    // Отправка обновленных данных и аватара
    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        try {
            // 1) Обновляем профиль (без аватара)
            const res = await api.put(`/api/users/update/${formData.id}`, {
                username: formData.username,
                age: formData.age,
                gender: formData.gender,
                password: formData.password,
            });
            console.log('Ответ сервера (обновление):', res);
            setUser({ ...user, ...res.data }); // Обновляем user в контексте

            // 2) Если выбран файл аватара — отправляем его
            if (avatarFile) {
                const avatarData = new FormData();
                avatarData.append('avatar', avatarFile);

                const avatarRes = await api.post(
                    `/api/users/uploadAvatar/${formData.id}`,
                    avatarData,
                    {
                        headers: {
                            'Content-Type': 'multipart/form-data',
                            Authorization: `Bearer ${user.token}`,
                        },
                    }
                );

                console.log('Ответ сервера (аватар):', avatarRes);

                // Обновим аватар в локальном user
                if (avatarRes.data.avatarUrl) {
                    setUser({ ...user, avatarUrl: avatarRes.data.avatarUrl });
                    setAvatarPreview(avatarRes.data.avatarUrl);
                }
            }

            addNotification('Данные успешно сохранены', 'success');
        } catch (err) {
            console.error('Ошибка при обновлении:', err.response || err);
            addNotification(err.response?.data?.error || 'Ошибка на сервере', 'error');
        }
    };

    return (
        <div className="containerforall max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md relative z-10">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Настройки</h2>
            <div className="settings-form">
                <div className="form-group">
                    <label>Аватар:</label>
                    {avatarPreview && (
                        <img
                            src={avatarPreview}
                            alt="Avatar preview"
                            className="avatar-preview w-24 h-24 rounded-full object-cover mb-2"
                        />
                    )}
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleAvatarChange}
                        className="mt-2"
                    />
                </div>
                <div className="form-group">
                    <label>Имя:</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                        className="input-field"
                    />
                </div>
                <div className="form-group">
                    <label>Возраст:</label>
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleChange}
                        required
                        className="input-field"
                    />
                </div>
                <div className="form-group">
                    <label>Пол:</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-field"
                    >
                        <option value="">Выбери пол</option>
                        <option value="male">Мужской</option>
                        <option value="female">Женский</option>
                        <option value="other">Другое</option>
                    </select>
                </div>
                <div className="form-group">
                    <label>Новый пароль:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        className="input-field"
                    />
                </div>
                <div className="notification-preferences mt-6">
                    <h3 className="text-lg font-semibold text-gray-700">Настройки уведомлений</h3>
                    <div className="flex items-center mt-2">
                        <input
                            type="checkbox"
                            id="inApp"
                            checked={notificationPreferences.inApp}
                            onChange={() => {
                                console.log('InApp checkbox toggled');
                                togglePreference('inApp');
                            }}
                            className="mr-2 cursor-pointer"
                        />
                        <label htmlFor="inApp" className="text-gray-600">Внутри приложения</label>
                    </div>
                    <div className="flex items-center mt-2">
                        <input
                            type="checkbox"
                            id="push"
                            checked={notificationPreferences.push}
                            onChange={() => {
                                console.log('Push checkbox toggled');
                                togglePreference('push');
                            }}
                            className="mr-2 cursor-pointer"
                        />
                        <label htmlFor="push" className="text-gray-600">Push-уведомления</label>
                    </div>
                </div>
                <button
                    type="button"
                    onClick={handleSubmit}
                    className="submit-button mt-4"
                >
                    Сохранить изменения
                </button>
                {message && <p className="text-green-500 mt-2">{message}</p>}
                {error && <p className="text-red-500 mt-2">{error}</p>}
            </div>
            <div className="notification-container fixed top-0 right-0 z-50">
                {notifications.map((notif) => (
                    <Notification
                        key={notif.id}
                        message={notif.message}
                        type={notif.type}
                        onClose={() => removeNotification(notif.id)}
                    />
                ))}
            </div>
        </div>
    );
}
