import React, { useState, useEffect } from 'react';
import { useAuth } from '../Account/AuthUser';
import Notification from '../components/Notification';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user } = useAuth();
    const [notification, setNotification] = useState({ message: '', type: '' });

    useEffect(() => {
        if (user) {
            if (!user.age || !user.gender) {
                setNotification({
                    message: 'Пожалуйста, укажите ваш возраст и пол в настройках профиля.',
                    type: 'warning'
                });
            } else {
                setNotification({ message: '', type: '' });
            }
        }
    }, [user]);

    if (!user) {
        return <div className="profile-container">Вы не вошли в систему</div>;
    }

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user.avatar ? SERVER_URL + user.avatar : '/default-avatar.png';

    return (
        <div className="profile-container">
            {notification.message && (
                <Notification
                    message={notification.message}
                    type={notification.type}
                    onClose={() => setNotification({ message: '', type: '' })}
                />
            )}

            <div className="profile-header">
                <img
                    src={avatarSrc}
                    alt="Avatar"
                    className="profile-avatar"
                    style={{ width: 150, height: 150, borderRadius: '50%', objectFit: 'cover' }}
                />
                <h2>Привет, {user.username}!</h2>
            </div>

            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Роль:</strong> {user.role || 'Пользователь'}</p>
            {user.age !== undefined && <p><strong>Возраст:</strong> {user.age}</p>}
            {user.gender !== undefined && <p><strong>Пол:</strong> {user.gender}</p>}
        </div>
    );
}
