import React, {useEffect, useState} from 'react';
import {useParams} from 'react-router-dom';
import api from '../Account/api';
import '../components/styles/Container.css';

export default function CourseDetails() {
    const {id} = useParams();
    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Новые состояния для формы комментария
    const [username, setUsername] = useState('');
    const [comment, setComment] = useState('');
    const [rating, setRating] = useState(5);
    const [submitError, setSubmitError] = useState(null);
    const [submitting, setSubmitting] = useState(false);

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                setLoading(true);
                setError(null);
                const res = await api.get(`/api/courses/${id}`);
                setCourse(res.data);
            } catch (err) {
                setError('Course not found or error loading data');
            } finally {
                setLoading(false);
            }
        };

        fetchCourse();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!username.trim() || !comment.trim()) {
            setSubmitError('Введите имя и комментарий');
            return;
        }

        setSubmitting(true);
        setSubmitError(null);

        try {
            // Предполагаем, что у API есть POST /api/courses/:id/reviews для добавления комментария
            const newReview = { username, comment, rating };
            await api.post(`/api/courses/${id}/reviews`, newReview);

            // Обновляем локально отзывы, чтобы сразу показать новый
            setCourse((prev) => ({
                ...prev,
                reviews: prev.reviews ? [...prev.reviews, { ...newReview, id: Date.now() }] : [{ ...newReview, id: Date.now() }],
            }));

            // Очистка формы
            setUsername('');
            setComment('');
            setRating(5);
        } catch (err) {
            setSubmitError('Ошибка при отправке комментария');
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) return <p>Loading course details...</p>;
    if (error) return <p>{error}</p>;
    if (!course) return <p>Course not found</p>;

    return (
        <>
            <div className="containerforall">
                <h2>{course.title}</h2>
                <img src={course.image} alt={course.title} style={{width: '300px', marginBottom: '15px'}}/>
                <p>{course.description}</p>
                <p>Category: {course.category}</p>
                <p>Price: ${course.price}</p>
            </div>

            <div className="containerforall" style={{marginTop: '30px'}}>
                <div className="reviews-section">
                    <h3>Отзывы</h3>
                    {course.reviews && course.reviews.length > 0 ? (
                        course.reviews.map((review) => (
                            <div key={review.id} style={{borderBottom: '1px solid #ccc', padding: '10px 0'}}>
                                <strong>{review.username || 'User'}</strong>: <span>{review.comment}</span>
                                <div>Rating: {review.rating} / 5</div>
                            </div>
                        ))
                    ) : (
                        <p>Комментариев пока нет.</p>
                    )}
                </div>

                <form onSubmit={handleSubmit} style={{marginTop: '20px'}}>
                    <h4>Оставить комментарий</h4>
                    {submitError && <p style={{color: 'red'}}>{submitError}</p>}

                    <div>
                        <label>
                            Имя:
                            <input
                                type="text"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                disabled={submitting}
                                required
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Комментарий:
                            <textarea
                                value={comment}
                                onChange={(e) => setComment(e.target.value)}
                                disabled={submitting}
                                required
                                rows={4}
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Рейтинг:
                            <select
                                value={rating}
                                onChange={(e) => setRating(Number(e.target.value))}
                                disabled={submitting}
                            >
                                {[5,4,3,2,1].map((r) => (
                                    <option key={r} value={r}>{r}</option>
                                ))}
                            </select>
                        </label>
                    </div>
                    <button type="submit" disabled={submitting}>
                        {submitting ? 'Отправка...' : 'Отправить'}
                    </button>
                </form>
            </div>
        </>
    );
}
