import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { Helmet } from 'react-helmet';
import debounce from 'lodash.debounce';
import '../components/styles/CourseCatalog.css';
import '../components/styles/Container.css';
import api from '../Account/api';

const CourseCatalog = () => {
    const { t } = useTranslation();
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [pagination, setPagination] = useState({ current: 1, pageSize: 6, total: 0 });
    const [filters, setFilters] = useState({ category: '', search: '', sort: 'newest' });

    // Debounced fetch to avoid too many calls при наборе
    const fetchCourses = useCallback(async () => {
        try {
            setLoading(true);
            setError(null);

            const params = {
                page: pagination.current,
                limit: pagination.pageSize,
                category: filters.category || undefined,
                search: filters.search || undefined,
                sort: filters.sort || undefined,
            };
            Object.keys(params).forEach((key) => params[key] === undefined && delete params[key]);

            const res = await api.get('/api/courses', { params });
            setCourses(res.data.courses);
            setPagination((prev) => ({ ...prev, total: Number(res.data.total) }));
            await new Promise((resolve) => setTimeout(resolve, 500));
        } catch (err) {
            setError(t('catalog.error'));
        } finally {
            setLoading(false);
        }
    }, [pagination.current, pagination.pageSize, filters, t]);

    // Debounce вызов fetchCourses для поиска
    const debouncedFetch = useCallback(debounce(fetchCourses, 500), [fetchCourses]);

    useEffect(() => {
        debouncedFetch();
        // Отмена debounce при размонтировании
        return () => debouncedFetch.cancel();
    }, [debouncedFetch]);

    // Звёздочки рейтинга (SVG для лучшего отображения и доступности)
    const renderStars = (rating) => {
        const fullStars = Math.floor(rating);
        const halfStar = rating % 1 >= 0.5;
        const emptyStars = 5 - fullStars - (halfStar ? 1 : 0);

        return (
            <span aria-label={`Rating: ${rating.toFixed(1)} out of 5 stars`} role="img">
        {Array(fullStars).fill('★').join('')}
                {halfStar && '☆'}
                {Array(emptyStars).fill('☆').join('')}
      </span>
        );
    };

    const totalPages = Math.ceil(pagination.total / pagination.pageSize);

    return (
        <>
            <Helmet>
                <title>{t('catalog.title')} - SkillUp</title>
                <meta
                    name="description"
                    content={t('catalog.metaDescription') || 'Browse programming, design, business and language courses on SkillUp platform.'}
                />
                <link rel="preconnect" href="https://your-api-domain.com" />
            </Helmet>

            <main className="containerforall" role="main">
                <section className="course-catalog" aria-label={t('catalog.title')}>
                    <header className="catalog-header">
                        <h1>{t('catalog.title')}</h1>
                        <form
                            className="catalog-controls"
                            role="search"
                            onSubmit={(e) => {
                                e.preventDefault();
                                setPagination((p) => ({ ...p, current: 1 }));
                                fetchCourses();
                            }}
                            aria-label={t('catalog.searchAriaLabel')}
                        >
                            <label htmlFor="search-input" className="visually-hidden">
                                {t('catalog.searchPlaceholder')}
                            </label>
                            <input
                                id="search-input"
                                type="search"
                                placeholder={t('catalog.searchPlaceholder')}
                                value={filters.search}
                                onChange={(e) => setFilters((f) => ({ ...f, search: e.target.value }))}
                                aria-label={t('catalog.searchAriaLabel')}
                            />

                            <label htmlFor="category-select" className="visually-hidden">
                                {t('catalog.category')}
                            </label>
                            <select
                                id="category-select"
                                value={filters.category}
                                onChange={(e) => {
                                    setFilters((f) => ({ ...f, category: e.target.value }));
                                    setPagination((p) => ({ ...p, current: 1 }));
                                }}
                                aria-label={t('catalog.category')}
                            >
                                <option value="">{t('catalog.allCategories')}</option>
                                <option value="programming">{t('catalog.programming')}</option>
                                <option value="design">{t('catalog.design')}</option>
                                <option value="business">{t('catalog.business')}</option>
                                <option value="language">{t('catalog.language')}</option>
                            </select>

                            <label htmlFor="sort-select" className="visually-hidden">
                                {t('catalog.sort')}
                            </label>
                            <select
                                id="sort-select"
                                value={filters.sort}
                                onChange={(e) => setFilters((f) => ({ ...f, sort: e.target.value }))}
                                aria-label={t('catalog.sort')}
                            >
                                <option value="newest">{t('catalog.newest')}</option>
                                <option value="alphabetical">{t('catalog.alphabetical')}</option>
                            </select>

                            <button type="submit" aria-label={t('catalog.refresh')}>
                                {t('catalog.refresh')}
                            </button>
                        </form>
                    </header>

                    {error && (
                        <div role="alert" className="error-box">
                            {error}
                        </div>
                    )}

                    {loading ? (
                        <p className="loading" aria-live="polite">
                            {t('catalog.loading')}
                        </p>
                    ) : (
                        <>
                            <section className="courses-grid" aria-live="polite" aria-relevant="additions removals">
                                {courses.length > 0 ? (
                                    courses.map((c) => (
                                        <article key={c.id} className="course-card" tabIndex="0" aria-label={`${c.title}, ${c.category}, ${c.price} dollars`}>
                                            <img loading="lazy" src={c.image} alt={c.title} width="300" height="180" />
                                            <h2>{c.title}</h2>
                                            <p>{c.description.slice(0, 80)}...</p>
                                            <div className="rating">
                                                <strong>{t('catalog.rating')}:</strong>{' '}
                                                {c.averageRating ? renderStars(c.averageRating) : t('catalog.noRatings')}
                                            </div>
                                            <footer className="card-footer">
                                                <span>{c.category}</span>
                                                <span>${c.price}</span>
                                            </footer>
                                            <nav className="card-actions" aria-label={`${c.title} actions`}>
                                                <button className="btn" aria-label={`${t('catalog.enroll')} ${c.title}`}>
                                                    {t('catalog.enroll')}
                                                </button>
                                                <Link to={`/Courses/${c.id}`} className="btn" aria-label={`${t('catalog.details')} ${c.title}`}>
                                                    {t('catalog.details')}
                                                </Link>
                                            </nav>
                                        </article>
                                    ))
                                ) : (
                                    <p>{t('catalog.noCoursesFound')}</p>
                                )}
                            </section>

                            <nav
                                className="pagination-controls"
                                role="navigation"
                                aria-label={t('catalog.pagination')}
                            >
                                <button
                                    disabled={pagination.current === 1}
                                    onClick={() => setPagination((p) => ({ ...p, current: p.current - 1 }))}
                                    aria-disabled={pagination.current === 1}
                                    aria-label={t('catalog.previous')}
                                >
                                    {t('catalog.previous')}
                                </button>

                                <span aria-live="polite">
                  {t('catalog.page', { page: pagination.current, total: totalPages || 1 })}
                </span>

                                <button
                                    disabled={pagination.current === totalPages || totalPages === 0}
                                    onClick={() => setPagination((p) => ({ ...p, current: p.current + 1 }))}
                                    aria-disabled={pagination.current === totalPages || totalPages === 0}
                                    aria-label={t('catalog.next')}
                                >
                                    {t('catalog.next')}
                                </button>
                            </nav>
                        </>
                    )}
                </section>
            </main>
        </>
    );
};

export default CourseCatalog;
