import React, { useState } from 'react';
import { useAuth } from '../Account/AuthUser';
import axios from 'axios';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user, setUser } = useAuth(); // Предполагаем, что setUser доступен
    const [selectedFile, setSelectedFile] = useState(null);

    if (!user) {
        return <div className="profile-container">Not logged in</div>;
    }

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleUpload = async () => {
        if (!selectedFile) return;

        const formData = new FormData();
        formData.append('avatar', selectedFile);

        try {
            const response = await axios.post(
                `http://localhost:5000/api/users/upload-avatar/${user.id}`,
                formData,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                }
            );
            // Обновляем аватар в интерфейсе
            if (response.data.avatar) {
                setUser({ ...user, avatar: response.data.avatar });
            }
        } catch (error) {
            console.error('Upload failed:', error);
        }
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img
                    src={user.avatar}
                    alt="Avatar"
                    className="profile-avatar"
                />
                <h2>Hi, {user.username}!</h2>
            </div>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role || 'User'}</p>
            {user.age !== undefined && <p><strong>Age:</strong> {user.age}</p>}
            {user.gender && <p><strong>Gender:</strong> {user.gender}</p>}

            <div className="avatar-upload">
                <input type="file" accept="image/*" onChange={handleFileChange} />
                <button onClick={handleUpload}>Upload Avatar</button>
            </div>
        </div>
    );
}
