import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../Account/api';
import '../components/styles/Container.css';
import { useAuth } from '../Account/AuthUser';

export default function CourseDetails() {
    const { id } = useParams();
    const { user } = useAuth(); // Текущий пользователь из твоего auth-хука

    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [comment, setComment] = useState('');
    const [submitError, setSubmitError] = useState(null);
    const [submitting, setSubmitting] = useState(false);

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                setLoading(true);
                setError(null);
                const res = await api.get(`/api/courses/${id}`);
                setCourse(res.data);
            } catch (err) {
                setError('Course not found or error loading data');
            } finally {
                setLoading(false);
            }
        };

        fetchCourse();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!comment.trim()) {
            setSubmitError('Комментарий не может быть пустым');
            return;
        }
        if (!user) {
            setSubmitError('Вы должны быть авторизованы, чтобы оставить комментарий');
            return;
        }

        setSubmitting(true);
        setSubmitError(null);

        try {
            const newReview = {
                username: user.username,
                comment,
                rating: 5, // если нужна, иначе убери
            };
            await api.post(`/api/courses/${id}/reviews`, newReview);

            // Добавим новый комментарий локально сразу
            setCourse((prev) => ({
                ...prev,
                reviews: prev.reviews ? [...prev.reviews, { ...newReview, id: Date.now() }] : [{ ...newReview, id: Date.now() }],
            }));

            setComment('');
        } catch (err) {
            setSubmitError('Ошибка при отправке комментария');
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) return <p>Loading course details...</p>;
    if (error) return <p>{error}</p>;
    if (!course) return <p>Course not found</p>;

    return (
        <>
            <div className="containerforall">
                <h2>{course.title}</h2>
                <img src={course.image} alt={course.title} style={{ width: '300px', marginBottom: '15px' }} />
                <p>{course.description}</p>
                <p>Category: {course.category}</p>
                <p>Price: ${course.price}</p>
            </div>

            <div className="containerforall" style={{ marginTop: '30px' }}>
                <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
                    <h4>Оставить комментарий</h4>
                    {submitError && <p style={{ color: 'red' }}>{submitError}</p>}

                    <div>
          <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              disabled={submitting}
              required
              rows={4}
              placeholder="Ваш комментарий..."
              style={{ width: '1000px', maxWidth: '50%' }} // Уменьшили ширину textarea
          />
                    </div>

                    <button type="submit" disabled={submitting || !user}>
                        {submitting ? 'Отправка...' : 'Отправить'}
                    </button>
                    {!user && <p style={{ color: 'red', marginTop: '10px' }}>Войдите, чтобы оставить комментарий</p>}
                </form>

                <div className="reviews-section">
                    <h3>Отзывы</h3>
                    {course.reviews && course.reviews.length > 0 ? (
                        course.reviews.map((review) => (
                            <div key={review.id} style={{ borderBottom: '1px solid #ccc', padding: '10px 0' }}>
                                <strong>{review.username || 'User'}</strong>: <span>{review.comment}</span>
                            </div>
                        ))
                    ) : (
                        <p>Комментариев пока нет.</p>
                    )}
                </div>
            </div>
        </>
    );

}
