import React from 'react';
import './styles/Notification.css';

export default function Notification({message, type, onClose}) {
    if (!message) return null;

    return (
        <div className={`notification ${type}`}>
            <span>{message}</span>
            <button onClick={onClose}>&times;</button>
        </div>
    );
}
