import React, { useState } from 'react';
import { useAuth } from '../Account/AuthUser';
import '../components/styles/Profile.css';

export default function Profile() {
    const { user } = useAuth();
    const [notificationPermission, setNotificationPermission] = useState(Notification.permission);

    if (!user) {
        return <div className="profile-container">Not logged in</div>;
    }

    const SERVER_URL = 'http://localhost:5000';
    const avatarSrc = user.avatar ? SERVER_URL + user.avatar : '/default-avatar.png';

    // Проверяем настройки push-уведомлений
    const canPushNotify = user.notification?.push === true;

    const enableNotifications = () => {
        if (!canPushNotify) {
            alert("Push уведомления отключены в настройках пользователя.");
            return;
        }

        if (!("Notification" in window)) {
            alert("Ваш браузер не поддерживает уведомления.");
            return;
        }

        if (Notification.permission === "granted") {
            showNotification();
        } else if (Notification.permission !== "denied") {
            Notification.requestPermission().then(permission => {
                setNotificationPermission(permission);
                if (permission === "granted") {
                    showNotification();
                }
            });
        }
    };

    const showNotification = () => {
        new Notification("Привет!", {
            body: `Добро пожаловать, ${user.username}!`,
            icon: avatarSrc,
        });
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img
                    src={avatarSrc}
                    alt="Avatar"
                    className="profile-avatar"
                    style={{ width: 150, height: 150, borderRadius: '50%', objectFit: 'cover' }}
                />
                <h2>Hi, {user.username}!</h2>
            </div>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role || 'User'}</p>
            {user.age !== undefined && <p><strong>Age:</strong> {user.age}</p>}
            {user.gender !== undefined && <p><strong>Gender:</strong> {user.gender}</p>}

            {/* Кнопка показывается, если push уведомления включены в настройках */}
            {canPushNotify ? (
                <button onClick={enableNotifications} disabled={notificationPermission === 'granted'}>
                    {notificationPermission === 'granted' ? 'Уведомления включены' : 'Включить уведомления'}
                </button>
            ) : (
                <p>Push-уведомления отключены в настройках.</p>
            )}
        </div>
    );
}
